create view CheckingOutToday (guestNo, guestName, guestAddress, roomNo, numDays, totalCost) as select g.guestNo, g.guestName, g.guestAddress, r.roomNo, datediff(b.dateTo, b.dateFrom) as numDays, (r.price * datediff(b.dateTo, b.dateFrom)) as totalCost from Booking b inner join Guest g on b.guestNo = g.guestNo inner join Hotel h on b.hotelNo = h.hotelNo inner join Room r on b.hotelNo = r.hotelNo and b.roomNo = r.roomNo where h.hotelName = 'Grosvenor Hotel' and b.dateTo = curdate();
